import React, { useEffect, useState } from "react";
import "../../Style/PostTd.css";
// 밑에는 편집, 삭제 아이콘 사용을 위해 import함
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-regular-svg-icons";
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

interface PostTdProps {
  item: PostItem;
  user_id: any;
}

const PostTd: React.FC<PostTdProps> = ({ item, user_id }) => {
  const onRemove = () => {
    alert("del");
  };

  const onEdit = () => {
    alert("Edit" + user_id + item.id);
  };
  // 여기에 sql 데이터 연동 편집 기능 넣기

  const [del_edit, setDelEdit] = useState<boolean>(true);
  function id_check() {
    return new Promise((resolve, reject) => {
      if (user_id === item.id) {
        setDelEdit(true);
      } else {
        setDelEdit(false);
      }
    });
  }

  useEffect(() => {
    const load = async () => {
      await id_check();
    };
    load();
  }, []);

  return (
    <tr className="tr2">
      <tbody className="tb">{item.order}</tbody>
      <tbody className="tb">{item.name}</tbody>
      <tbody className="tb">{item.date}</tbody>
      <tbody className="tb">{item.title}</tbody>
      <tbody className="tb">{item.content}</tbody>
      <button disabled={del_edit} onClick={onEdit} className="edit_btn">
        <FontAwesomeIcon icon={faEdit} />
      </button>
      <tbody onClick={onRemove} className="remove_btn">
        <FontAwesomeIcon icon={faTrashAlt} />
      </tbody>
    </tr>
  );
};

export default PostTd;
