import React, { useState } from "react";
import "../Style/PostModal.css";

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

interface PostModalProps {
  selectedData: PostItem;
}

const PostModal: React.FC = () => {
  const [isHovering, setIsHovering] = useState(false);

  const handleMouseOver = () => {
    setIsHovering(true);
  };

  const handleMouseOut = () => {
    setIsHovering(false);
  };

  return (
    <div className="list">
      <div className="list_box">
        <div className="list_border">
          <h4 className="h4">게시글 정보 수정하기</h4>
          <i className="fas fa-items cursor-pointer"></i>
        </div>
        <form>
          <div className="p-3">
            <div>ID: {""}</div>
            <div>
              작성자:{" "}
              <input
                className="contents2"
                type="text"
                name="name"
                value={"edited.name"}
              />
            </div>
            <div>
              작성날짜:{" "}
              <input
                className="contents2"
                type="date"
                name="date"
                value={"edited.date"}
              />
            </div>
            <div>
              제목:{" "}
              <input
                className="contents2"
                type="text"
                name="title"
                value={"edited.title"}
              />
            </div>
            <div>
              작성내용:{" "}
              <input
                className="contents2"
                type="text"
                name="content"
                value={"edited.content"}
              />
            </div>
          </div>
          <div className="btn_func">
            <button
              className={isHovering ? "cancel_btn_hover" : "cancel_btn"}
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              취소
            </button>
            <button
              type="submit"
              className={isHovering ? "modify_btn_hover" : "modify_btn"}
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
            >
              수정
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PostModal;
