import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link, Route, Routes, Navigate } from "react-router-dom";
import "./create_address.css";

interface IProps {}

const IdList = ({
  id,
  phone,
  pass,
}: {
  id: string;
  phone: string;
  pass: string;
}) => {
  return (
    <tr>
      <td>{id}</td>
      <td>{phone}</td>
      <td>{pass}</td>
    </tr>
  );
};

class Find_id extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      phone: "",
      id: "",
      memberIdList: [],
    };
  }

  state = {
    phone: "",
    id: "",
    memberIdList: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  check = () => {
    Axios.post("http://localhost:8000/find_pass", {
      phone: this.state.phone,
      id: this.state.id,
    })
      .then((res) => {
        if (res.data[0] === undefined) {
          alert("실패했습니다.");
          const { data } = res;
          this.setState({
            memberIdList: data,
          });
        } else {
          alert("성공했습니다.");
          const { data } = res;
          this.setState({
            memberIdList: data,
          });
        }
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    const { memberIdList }: { memberIdList: any } = this.state;
    return (
      <body>
        <div className="box">
          <form method="post" className="create-address-form">
            <fieldset className="inner-form">
              <legend className="create_address-member">비밀번호 찾기</legend>
              <div>
                <input
                  type="text"
                  name="id"
                  value={this.state.id}
                  onChange={this.handleChange}
                  placeholder="ID를 입력해주세요"
                ></input>
              </div>
              <div>
                <input
                  type="text"
                  name="phone"
                  value={this.state.phone}
                  onChange={this.handleChange}
                  placeholder="전화번호를 입력해주세요"
                ></input>
                <Button variant="info" onClick={this.check}>
                  확인
                </Button>
              </div>
              <tbody>
                {memberIdList.map((v: any) => {
                  return (
                    <IdList
                      id={v.member_id}
                      phone={v.phone}
                      pass={v.member_pass}
                    />
                  );
                })}
              </tbody>
              <Link to="/">
                <Button variant="info">종료</Button>
              </Link>
            </fieldset>
          </form>
        </div>
      </body>
    );
  }
}

export default Find_id;
