import { Link, useLocation } from "react-router-dom";
import character_img from "../image/character_img.jpg"; // 이미지 불러온 것
import Axios from "axios";
import { useState } from "react";
import Button from "react-bootstrap/Button";
import "../style/info_modify.css";

function Info_modify() {
  const location = useLocation();
  const [curpass, set_curpass] = useState(location.state.member_pass);
  const [curphone, set_curphone] = useState(location.state.phone);
  const [curgender, set_curgender] = useState(location.state.gender);
  const [curnickname, set_curnickname] = useState(location.state.nickname);
  const [curSSN, set_curSSN] = useState(location.state.SSN);
  const [curintro, set_curintro] = useState(location.state.intro);
  const [fcheck_pass, set_fcheck_pass] = useState("");
  const [scheck_pass, set_scheck_pass] = useState("");
  const [change_pass, set_change_pass] = useState("");
  const [phone, set_phone] = useState("");
  const [gender, set_gender] = useState("1");
  const [nickname, set_nickname] = useState("");
  const [SSN, set_SSN] = useState("");
  const [intro, set_intro] = useState(location.state.intro);

  const logout = () => {
    document.location.href = "/";
  };

  const first_handleChange = (e: any) => {
    set_fcheck_pass(e.target.value);
  };

  const second_handleChange = (e: any) => {
    set_scheck_pass(e.target.value);
  };

  const change_handleChange = (e: any) => {
    set_change_pass(e.target.value);
  };

  const phone_handleChange = (e: any) => {
    set_phone(e.target.value);
  };

  const gender_handleChange = (e: any) => {
    set_gender(e.target.value);
  };

  const nickname_handleChange = (e: any) => {
    set_nickname(e.target.value);
  };

  const SSN_handleChange = (e: any) => {
    set_SSN(e.target.value);
  };

  const change_password = () => {
    if (curpass === fcheck_pass) {
      if (scheck_pass === change_pass) {
        Axios.post("http://localhost:8000/info_modify/change_pass", {
          id: location.state.member_id,
          change_pass: change_pass,
        })
          .then((res) => {
            alert("비밀번호를 변경했습니다.");
            set_curpass(change_pass);
            set_fcheck_pass("");
            set_scheck_pass("");
            set_change_pass("");
          })
          .catch((e) => {
            console.error(e);
          });
      } else {
        alert("변경할 비밀번호가 다릅니다.");
      }
    } else {
      alert("현재 비밀번호가 다릅니다.");
    }
  };

  const info_change = () => {
    Axios.post("http://localhost:8000/info_modify/info", {
      id: location.state.member_id,
      phone: phone,
      gender: gender,
      nickname: nickname,
      SSN: SSN,
    })
      .then((res) => {
        alert("정보 수정 완료");
        set_curphone(phone);
        set_curgender(gender);
        set_curnickname(nickname);
        set_curSSN(SSN);
        set_phone("");
        set_nickname("");
        set_SSN("");
      })
      .catch((e) => {
        console.error(e);
      });
  };
  const save_intro = () => {
    Axios.post("http://localhost:8000/save_intro", {
      id: location.state.member_id,
      intro: intro,
    })
      .then((res) => {
        alert("저장했습니다.");
        set_curintro(intro);
        set_intro(intro);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const intro_handleChange = (e: any) => {
    set_intro(e.target.value);
  };
  return (
    <section>
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">{curnickname}</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                name="intro"
                className="introduction-text"
                placeholder="자기소개를 작성하세요"
                value={intro}
                onChange={intro_handleChange}
              ></textarea>
              <br></br>
              <input type="submit" value="수정" onClick={save_intro}></input>
            </div>
            <div>
              <button type="submit" value="logout" onClick={logout}>
                logout
              </button>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link
          to={"/main/Info_modify"}
          state={{
            member_id: location.state.member_id,
            member_pass: curpass,
            phone: curphone,
            gender: curgender,
            nickname: curnickname,
            SSN: curSSN,
            intro: intro,
          }}
        >
          정보 수정
        </Link>
        <Link
          to={"/main/Schedule"}
          state={{
            member_id: location.state.member_id,
            member_pass: curpass,
            phone: curphone,
            gender: curgender,
            nickname: curnickname,
            SSN: curSSN,
            intro: intro,
          }}
        >
          일정
        </Link>
        <Link
          to={"/main/Products"}
          state={{
            member_id: location.state.member_id,
            member_pass: curpass,
            phone: curphone,
            gender: curgender,
            nickname: curnickname,
            SSN: curSSN,
            intro: intro,
          }}
        >
          상품
        </Link>
      </nav>
      <form className="box1">
        <fieldset className="inner-info-modify-form1">
          <legend className="info-modify-title">비밀번호 변경</legend>
          <div className="info-modify-pass-section">
            <span className="info-modify-subtitle2">이전 비밀번호 : </span>
            <input
              type="password"
              name="fcheck_pass"
              className="info-modify_pass"
              placeholder="기존 비밀번호를 입력해주세요"
              value={fcheck_pass}
              onChange={first_handleChange}
            ></input>
          </div>
          <div>
            <span className="info-modify-subtitle3">변경 할 비밀번호 : </span>
            <input
              type="password"
              name="scheck_pass"
              className="info-modify_pass"
              placeholder="변경할 비밀번호를 입력해주세요"
              value={scheck_pass}
              onChange={second_handleChange}
            ></input>
          </div>
          <div>
            <span className="info-modify-subtitle4">
              변경 할 비밀번호(확인) :{" "}
            </span>
            <input
              type="password"
              name="change_pass"
              className="info-modify_pass"
              placeholder="변경할 비밀번호를 입력해주세요"
              value={change_pass}
              onChange={change_handleChange}
            ></input>
          </div>
          <Button
            variant="info"
            onClick={change_password}
            className="info-modify-pass-section-btn"
          >
            확인
          </Button>
        </fieldset>
        <fieldset className="inner-info-modify-form2">
          <legend className="info-modify-title">개인정보 변경</legend>
          <div className="info-modify-nickname-section">
            <span className="info-modify-subtitle1">닉네임 : </span>
            <input
              type="text"
              name="nickname"
              className="info-modify-nickname"
              placeholder="변경할 닉네임을 입력해주세요"
              value={nickname}
              onChange={nickname_handleChange}
            ></input>
          </div>
          <div>
            <span className="info-modify-subtitle">전화번호 : </span>
            <input
              type="text"
              name="phone"
              className="info-modify-phone"
              placeholder="변경할 전화번호를 입력해주세요"
              value={phone}
              onChange={phone_handleChange}
            ></input>
          </div>
          <div>
            <span className="info-modify-subtitle">주민번호 : </span>
            <input
              type="text"
              name="SSN"
              className="info-modify-SSN"
              placeholder="주민번호를 입력해주세요"
              value={SSN}
              onChange={SSN_handleChange}
            ></input>
            <div>
              <label className="man">
                남
                <input
                  type="radio"
                  name="gender"
                  value="1"
                  onChange={gender_handleChange}
                ></input>
              </label>
              <label className="girl">
                여
                <input
                  type="radio"
                  name="gender"
                  value="0"
                  onChange={gender_handleChange}
                ></input>
              </label>
            </div>
          </div>
          <Button
            variant="info"
            onClick={info_change}
            className="info-modify-btn"
          >
            확인
          </Button>
        </fieldset>
      </form>
    </section>
  );
}

export default Info_modify;
