import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link } from "react-router-dom";
import "../style/create_address.css";

interface IProps {}

class Login_form extends Component<IProps> {
  /**
   * @return {Component} Component
   */
  constructor(props: any) {
    super(props);
    this.state = {
      id_check: "0",
      member_id: "",
      member_pass: "",
      gender: "1",
      phone: "",
      nickname: "",
      SSN: "",
    };
  }

  state = {
    id_check: "",
    member_id: "",
    member_pass: "",
    gender: "",
    phone: "",
    nickname: "",
    SSN: "",
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    if (this.state.id_check === "0") {
      alert("중복 확인을 해주세요");
    } else {
      if (
        this.state.member_id === "" ||
        this.state.member_pass === "" ||
        this.state.phone === "" ||
        this.state.nickname === "" ||
        this.state.SSN === ""
      ) {
        alert("모든 곳을 채우세요");
      } else {
        Axios.post("http://localhost:8000/member_update", {
          member_id: this.state.member_id,
          member_pass: this.state.member_pass,
          gender: this.state.gender,
          phone: this.state.phone,
          nickname: this.state.nickname,
          SSN: this.state.SSN,
        })
          .then((res) => {
            this.setState({
              member_id: "",
              member_pass: "",
              genber: "",
              phone: "",
              nickname: "",
              SSN: "",
            });
            alert("회원가입이 완료되었습니다.");
            document.location.href = "/";
          })
          .catch((e) => {
            console.error(e);
          });
      }
    }
  };

  same_id_check = () => {
    Axios.post("http://localhost:8000/id_check", {
      id: this.state.member_id,
    }).then((res) => {
      if (res.data[0] === undefined) {
        this.setState({
          id_check: "1",
        });
        alert("사용 가능합니다.");
      } else {
        this.setState({
          id_check: "0",
        });
        alert("아이디가 중복됩니다.\n다시 입력해주세요.");
      }
    });
  };

  render() {
    return (
      <body>
        <div className="box">
          <form className="create-address-form">
            <fieldset className="inner-form">
              <legend className="title">회원가입</legend>
              <div className="member_id_title">
                <span className="subtitle1">아이디 : </span>
                <div className="member_id_section">
                  <input
                    type="text"
                    name="member_id"
                    className="member_id"
                    placeholder="아이디를 입력해주세요"
                    value={this.state.member_id}
                    onChange={this.handleChange}
                  ></input>
                  <button
                    type="button"
                    className="member_id_check_btn"
                    onClick={this.same_id_check}
                  >
                    중복
                  </button>
                </div>
              </div>
              <div>
                <span className="subtitle">비밀번호 : </span>
                <input
                  type="password"
                  name="member_pass"
                  className="member_pass"
                  placeholder="비밀번호를 입력해주세요"
                  value={this.state.member_pass}
                  onChange={this.handleChange}
                ></input>
              </div>
              <div>
                <span className="subtitle1">닉네임 : </span>
                <input
                  type="text"
                  name="nickname"
                  className="create_nickname"
                  placeholder="닉네임을 입력해주세요"
                  value={this.state.nickname}
                  onChange={this.handleChange}
                ></input>
              </div>
              <div>
                <span className="subtitle">전화번호 : </span>
                <input
                  type="text"
                  name="phone"
                  className="phone"
                  placeholder="전화번호를 입력해주세요"
                  value={this.state.phone}
                  onChange={this.handleChange}
                ></input>
              </div>
              <div className="SSN_title">
                <span className="subtitle">주민번호 : </span>
                <input
                  type="text"
                  name="SSN"
                  className="SSN"
                  placeholder="주민번호를 입력해주세요"
                  value={this.state.SSN}
                  onChange={this.handleChange}
                ></input>
                <div>
                  <label className="man">
                    남
                    <input
                      type="radio"
                      value="1"
                      name="gender"
                      onChange={this.handleChange}
                    ></input>
                  </label>
                  <label className="girl">
                    여
                    <input
                      type="radio"
                      value="0"
                      name="gender"
                      onChange={this.handleChange}
                    ></input>
                  </label>
                </div>
              </div>
            </fieldset>
            <div>
              <Button variant="info" onClick={this.update} className="btn">
                확인
              </Button>
              <Link to="/">
                <Button variant="info" className="btn">
                  종료
                </Button>
              </Link>
            </div>
          </form>
        </div>
      </body>
    );
  }
}

export default Login_form;
