import { Link, NavLink } from "react-router-dom"; // 스타일을 주기위한 것
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import Button from "react-bootstrap/Button";
import Axios from "axios";
const express = require("express");
const app = express();
const mysql = require("mysql");
const PORT = process.env.port || 8000;
const cors = require("cors");
const bodyParser = require("body-parser");
const { isElementAccessExpression } = require("typescript");

app.use(express.json());
app.use(bodyParser.urlencoded({ extended: true }));

let corsOptions = {
  origin: "*",
  credential: true,
};

app.use(cors(corsOptions));
const db = mysql.createPool({
  host: "localhost",
  user: "root",
  password: "0000",
  database: "health",
});

app.listen(PORT, () => {
  console.log(`running on port ${PORT}`);
});

app.post("/member_update", (req, res) => {
  var member_id = req.body.member_id;
  var member_pass = req.body.member_pass;
  var gender = req.body.gender;
  var phone = req.body.phone;
  var nickname = req.body.nickname;
  var SSN = req.body.SSN;

  const sqlQuery =
    "insert into member(member_id, member_pass, gender, phone, nickname, SSN) values (?, ?, ?, ?, ?, ?);";
  db.query(
    sqlQuery,
    [member_id, member_pass, gender, phone, nickname, SSN],
    (err, result) => {
      if (err) {
        console.log(err);
      } else {
        res.send(result);
      }
    }
  );
});

app.post("/find_id", (req, res) => {
  var phone = req.body.phone;
  const sqlQuery = "select member_id, phone from member where phone = ?;";
  db.query(sqlQuery, [phone], (err, result) => {
    if (err) {
      console.log(err);
    } else {
      res.send(result);
    }
  });
});

app.post("/find_pass", (req, res) => {
  var phone = req.body.phone;
  var id = req.body.id;
  const sqlQuery =
    "select member_id, phone, member_pass from member where phone = ? AND member_id = ?;";
  db.query(sqlQuery, [phone, id], (err, result) => {
    if (err) {
      console.log(err);
    } else {
      res.send(result);
    }
  });
});

app.post("/login", (req, res) => {
  var id = req.body.id;
  var pass = req.body.pass;
  const sqlQuery =
    "select member_id, phone from member where member_id = ? AND member_pass = ?;";
  db.query(sqlQuery, [id, pass], (err, result) => {
    if (err) {
      console.log("err");
    } else {
      res.send(result);
      app.get(`/use_id`, (req, res) => {
        const sqlQuery = "select * from member where member_id = ?;";
        db.query(sqlQuery, [id], (err, result) => {
          if (err) {
            console.log(err);
          } else {
            res.send(result);
          }
        });
      });
    }
  });
});

app.post("/id_check", (req, res) => {
  var id = req.body.id;

  const sqlQuery = "select member_id from member where member_id = ?;";
  db.query(sqlQuery, [id], (err, result) => {
    if (err) {
      console.log("err");
    } else {
      res.send(result);
    }
  });
});

const check = () => {
  Axios.post("http://localhost:3000/main", {
    id: "",
    pass: "",
  })
    .then((res) => {
      alert("로그아웃");
    })
    .catch((e) => {
      console.error(e);
    });
};

export const SideMenu = () => (
  <div className="side-menu">
    <div className="info">
      <div className="character">
        <img src={character_img} className="img1" alt="character_img.jpg" />
      </div>
      <div className="nickname">닉네임</div>
      <div className="info-script">
        <div className="introduction">자기소개</div>
        <textarea
          className="introduction-text"
          placeholder="자기소개를 작성하세요"
        ></textarea>
        <br></br>
        <input type="submit" value="수정"></input>
        <input type="submit" value="저장"></input>
      </div>
      <div>
        <input type="submit" value="logout" onClick={check}></input>
      </div>
    </div>
  </div>
);

export const TopMenu = () => (
  <nav className="top-menu">
    <NavLink to="/main/Info_modify">정보 수정</NavLink>
    <NavLink to="/main/Schedule">일정</NavLink>
    <NavLink to="/main/Products">상품</NavLink>
  </nav>
);
