import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link, Route, Routes, Navigate } from "react-router-dom";
import "./create_address.css";

interface IProps {}

class Login_form extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      member_id: "",
      member_pass: "",
      gender: "1",
      phone: "",
      nickname: "",
      SSN: "",
    };
  }

  state = {
    member_id: "",
    member_pass: "",
    gender: "",
    phone: "",
    nickname: "",
    SSN: "",
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  update = () => {
    Axios.post("http://localhost:8000/member_update", {
      member_id: this.state.member_id,
      member_pass: this.state.member_pass,
      gender: this.state.gender,
      phone: this.state.phone,
      nickname: this.state.nickname,
      SSN: this.state.SSN,
    })
      .then((res) => {
        this.setState({
          member_id: "",
          member_pass: "",
          gender: "",
          phone: "",
          nickname: "",
          SSN: "",
        });
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    return (
      <body>
        <div className="box">
          <form method="post" className="create-address-form">
            <fieldset className="inner-form">
              <legend className="create_address-member">회원가입</legend>
              <input
                type="text"
                name="member_id"
                value={this.state.member_id}
                onChange={this.handleChange}
                placeholder="아이디를 입력해주세요"
              ></input>
              <input
                type="pass"
                name="member_pass"
                value={this.state.member_pass}
                onChange={this.handleChange}
                placeholder="비밀번호를 입력해주세요"
              ></input>
              <input
                type="text"
                name="nickname"
                value={this.state.nickname}
                onChange={this.handleChange}
                placeholder="닉네임을 입력해주세요"
              ></input>
              <input
                type="text"
                name="phone"
                value={this.state.phone}
                onChange={this.handleChange}
                placeholder="전화번호를 입력해주세요"
              ></input>
              <input
                type="text"
                name="SSN"
                value={this.state.SSN}
                onChange={this.handleChange}
                placeholder="주민번호를 입력해주세요"
              ></input>
              <div>
                <label className="create_address-man">
                  남
                  <input
                    type="radio"
                    value="1"
                    name="gender"
                    onChange={this.handleChange}
                    checked
                  ></input>
                </label>
                <label className="create_address-girl">
                  여
                  <input
                    type="radio"
                    value="0"
                    name="gender"
                    onChange={this.handleChange}
                  ></input>
                </label>
              </div>
            </fieldset>
            <Link to="/">
              <Button variant="info" onClick={this.update}>
                확인
              </Button>
            </Link>
          </form>
        </div>
      </body>
    );
  }
}

export default Login_form;
