import "./App.css";
import Login_form from "./login_form";
import Create_address from "./create_address";
import Find_id from "./find_id";
import { BrowserRouter, Route, Routes } from "react-router-dom";

function App() {
  handleModify = (checkList: any) => {
    if (checkList.length === 0) {
      alert("수정할 게시글을 선택하세요.");
    } else if (checkList.length > 1) {
      alert("하나의 게시글만 선택하세요.");
    }

    this.setState({
      isModifyMode: checkList.length === 1,
    });

    this.setState({
      boardId: checkList[0] || 0,
    });
  };

  handleCancel = () => {
    this.setState({
      isModifyMode: false,
      isComplete: false,
      boardId: 0,
    });
  };

  renderComplete = () => {
    this.setState({
      isComplete: true,
    });
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Login_form></Login_form>}></Route>

          <Route
            path="/create"
            element={<Create_address></Create_address>}
          ></Route>
          <Route
            path="/find_id"
            element={
              <Find_id>
                isModifyMode={this.state.isModifyMode}
                boardId={this.state.boardId}
                handleCancel={this.handleCancel}
              </Find_id>
            }
          ></Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
