import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link } from "react-router-dom";
import "./M_P.css";
import { useLocation } from "react-router-dom";
import { PageTemplate } from "./Menus";
import character_img from "./character_img.jpg"; // 이미지 불러온 것

function Main() {
  const location = useLocation();
  const type = location.state;
  const c = () => {
    console.log(location);
    console.log(type.using_id);
  };
  const check = () => {
    document.location.href = "/";
  };
  return (
    <PageTemplate>
      <section className="main">
        <div className="page">
          <div className="side-menu">
            <div className="info">
              <div className="character">
                <img
                  src={character_img}
                  className="img1"
                  alt="character_img.jpg"
                />
              </div>
              <div className="nickname">닉네임</div>
              <div className="info-script">
                <div className="introduction">자기소개</div>
                <textarea
                  className="introduction-text"
                  placeholder="자기소개를 작성하세요"
                ></textarea>
                <br></br>
                <input type="submit" value="수정"></input>
                <input type="submit" value="저장"></input>
              </div>
              <div>
                <input type="submit" value="logout" onClick={check}></input>
              </div>
            </div>
          </div>
          {children}
        </div>
        <nav className="top-menu">
          <Link to="/main/Info_modify">정보 수정</Link>
          <Link to="/main/Schedule">일정</Link>
          <Link to="/main/Products">상품</Link>
        </nav>
        <div>어서오세요</div>
      </section>
    </PageTemplate>
  );
}

export default Main;
