import { Component } from "react";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import "./login_form.css";
import Axios from "axios";

interface IProps {}

class Login_form extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      id: "",
      pass: "",
      memberIdList: [],
    };
  }

  state = {
    id: "",
    pass: "",
    memberIdList: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  check = () => {
    Axios.post("http://localhost:8000/login", {
      pass: this.state.pass,
      id: this.state.id,
    })
      .then((res) => {
        if (res.data[0] === undefined) {
          alert("로그인 실패");
          document.location.href = "/";
        } else {
          alert("로그인 성공");
          /*
          Axios.get("http://localhost:8000/use_id", {})
            .then((res) => {
              alert("cus");
            })
            .catch((e) => {
              console.error(e);
            });
            */
          <Link to="/main" state={{ using_id: id }}></Link>;
        }
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    return (
      <body>
        <div className="box">
          <div className="login-form">
            <h1>로그인</h1>
            <div className="login-top-section">
              <div className="login-input-section">
                <div>
                  <input
                    type="text"
                    name="id"
                    placeholder="아이디"
                    value={this.state.id}
                    onChange={this.handleChange}
                    className="login-id"
                  ></input>
                </div>
                <input
                  type="password"
                  name="pass"
                  placeholder="비밀번호"
                  value={this.state.pass}
                  onChange={this.handleChange}
                  className="login-password"
                ></input>
              </div>
              <form action="/main" method="get">
                <Button
                  type="submit"
                  className="login-btn"
                  onClick={this.check}
                >
                  로그인
                </Button>
              </form>
            </div>
            <div className="login-option">
              <form action="/create" method="get" className="option1">
                <Link to="/create">
                  <Button type="submit" className="option1-inner">
                    회원가입
                  </Button>
                </Link>
              </form>
              <form action="/id/search" method="get" className="option2">
                <Link to="/find_id">
                  <Button type="submit" className="option2-inner">
                    아이디 찾기
                  </Button>
                </Link>
              </form>
              <form action="/pw/search" method="get" className="option3">
                <Link to="/find_pass">
                  <Button type="submit" className="option3-inner">
                    비밀번호 찾기
                  </Button>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </body>
    );
  }
}
export default Login_form;
