import { Component } from "react";
import io from "socket.io-client";
const socket = io("http://localhost:3001");

class login extends Component {
  constructor(props: any) {
    super(props);
    this.state = {
      id: "", //id
      pw: "", //메세지 내용
      messageList: [], //메세지 리스트
    };
  }

  state = {
    id: "",
    pw: "",
    messageList: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  sendMsg = (e: any) => {
    e.preventDefault();
    socket.emit("login message", {
      id: this.state.id,
      pw: this.state.pw,
    });
    this.setState({
      id: "",
      pw: "",
    });
  };

  componentWillMount() {
    socket.on("receive message", (message) => {
      this.setState({
        messageList: [this.state.messageList, message],
      });
    });
  }

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            name="id"
            placeholder="id"
            value={this.state.id}
            onChange={this.handleChange}
          ></input>
          <input
            type="password"
            name="pw"
            placeholder="pw"
            value={this.state.pw}
            onChange={this.handleChange}
          ></input>
          <button type="submit" onClick={this.sendMsg}>
            전송
          </button>
        </form>
      </div>
    );
  }
}
export default login;
