import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../Style/PostBoard.css";
/* import TopMenu from './TopMenu'; */
import Login from "../Login";
import PostFunc from "../PostFunc";
import PostTr from "./Test_PostTr";
import PostModal from "./Test_PostModal";
import io from "socket.io-client";

import PostTd from "./Test_PostTd";

import "../../Style/PostTd.css";
// 밑에는 편집, 삭제 아이콘 사용을 위해 import함
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-regular-svg-icons";
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons";

const PostBoard_Data_Save = require("../Function/PostBoard_Data_Save.js");

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

const PostBoard: React.FC = () => {
  const [info, setInfo] = useState<PostItem[]>([]);
  const [selected, setSelected] = useState<PostItem | null>(null);
  const [modalOn, setModalOn] = useState<boolean>(false);
  const [cur_num, setCurNum] = useState<number>(1);

  const handleCancel = () => {
    setModalOn(false);
  };

  const handleEditSumit = (item: PostItem) => {
    setModalOn(false);
  };

  const location = useLocation();
  const userInfo = { ...location.state };

  const navigate = useNavigate();

  function Go_Back() {
    navigate("/test_page", {
      state: { id: userInfo.id },
    });
  }

  function use_save_data(data: any) {
    return new Promise((resolve, reject) => {
      var data_num = 0;
      while (true) {
        if (data.user_data[data_num] === undefined) {
          break;
        } else {
          console.log(data.user_data[data_num]);
          const post: PostItem = {
            order: data.user_data[data_num].note_order,
            id: data.user_data[data_num].note_id,
            name: data.user_data[data_num].note_name,
            date: data.user_data[data_num].note_date,
            title: data.user_data[data_num].note_title,
            content: data.user_data[data_num].note_content,
          };
          info[data_num] = post;
          data_num++;
        }
      }
      resolve(0);
    });
  }

  async function save_data() {
    const socket = io("http://localhost:3001", { transports: ["websocket"] });
    await PostBoard_Data_Save.Send_Notice_Five_Data(socket, cur_num);
    const data = await PostBoard_Data_Save.Rec_Notice_Five_Data(socket);
    console.log("start");
    await use_save_data(data);
    console.log(info);
    socket.disconnect();
  }

  useEffect(() => {
    save_data();
  }, []);

  const onRemove = () => {};

  const onEdit = () => {};

  return (
    <div className="body_box">
      <div className="bottom_box">
        <div className="left_box">
          <div className="main_area">
            <div className="post_area">
              <div className="post_table">
                <h1 className="h1">공지사항 게시글 리스트</h1>
                <table className="table">
                  <thead className="thead">
                    <tr className="tr1">
                      <th className="contents1">순번</th>
                      <th className="contents1">작성자</th>
                      <th className="contents1">작성날짜</th>
                      <th className="contents1">제목</th>
                      <th className="contents1">작성내용</th>
                      <th className="contents1">편집</th>
                      <th className="contents1">삭제</th>
                    </tr>
                  </thead>
                  <script>save_data();</script>
                  {info.map((item) => (
                    <tbody>
                      <tr className="tr2">
                        <tbody className="tb">{item.order}</tbody>
                        <tbody className="tb">{item.name}</tbody>
                        <tbody className="tb">{item.date}</tbody>
                        <tbody className="tb">{item.title}</tbody>
                        <tbody className="tb">{item.content}</tbody>
                        <tbody onClick={onEdit} className="edit_btn">
                          <FontAwesomeIcon icon={faEdit} />
                        </tbody>
                        <tbody onClick={onRemove} className="remove_btn">
                          <FontAwesomeIcon icon={faTrashAlt} />
                        </tbody>
                      </tr>
                    </tbody>
                  ))}
                </table>
              </div>
            </div>
            <div className="complain_area">
              <div className="complain_table">
                <h1 className="h1">불만사항 게시글 리스트</h1>
                <table className="table">
                  <thead className="thead">
                    <tr className="tr1">
                      <th className="contents1">순번</th>
                      <th className="contents1">작성자</th>
                      <th className="contents1">작성날짜</th>
                      <th className="contents1">제목</th>
                      <th className="contents1">작성내용</th>
                      <th className="contents1">편집</th>
                      <th className="contents1">삭제</th>
                    </tr>
                  </thead>
                </table>
              </div>
              <button type="button" onClick={Go_Back}>
                이전으로
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostBoard;
