import { Component } from "react";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import "./login_form.css";
import Axios from "axios";
import Create_address from "./create_address";

interface IProps {}

class Login_form extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      id: "",
      pass: "",
      memberIdList: [],
    };
  }

  state = {
    id: "",
    pass: "",
    memberIdList: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  check = () => {
    Axios.post("http://localhost:8000/login", {
      pass: this.state.pass,
      id: this.state.id,
    })
      .then((res) => {
        const { data } = res;
        this.setState({
          memberIdList: data,
        });
        if (res.data[0].cnt === 1) {
          window.sessionStorage.setItem("id", idRef.current.value); // 세션스토리지에 key : id , value : idRef.current.value로 저장
          // sessionsStorage는 창 닫으면 사라짐, localStorage는 안사라짐
          navigate("/main");
        } else {
          alert("아이디, 패스워드가 정확하지 않습니다.");
          idRef.current.value = "";
          pwRef.current.value = "";
          navigate("/");
        }
        this.getList();
        alert("succss");
      })
      .catch((e) => {
        console.error(e);
      });
  };

  getList = () => {
    Axios.get(`http://localhost:8000/login`, {})
      .then((res) => {
        const { data } = res;
        this.setState({
          memberIdList: data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    return (
      <body>
        <div className="box">
          <div className="login-form">
            <h1>로그인</h1>
            <div className="login-top-section">
              <div className="login-input-section">
                <div>
                  <input
                    type="text"
                    name="id"
                    placeholder="아이디"
                    value={this.state.id}
                    onChange={this.handleChange}
                    className="login-id"
                  ></input>
                </div>
                <input
                  type="password"
                  name="pass"
                  placeholder="비밀번호"
                  value={this.state.pass}
                  onChange={this.handleChange}
                  className="login-password"
                ></input>
              </div>
              <form action="/main" method="get">
                <Link to="/main">
                  <Button
                    type="submit"
                    className="login-btn"
                    onClick={this.check}
                  >
                    로그인
                  </Button>
                </Link>
              </form>
            </div>
            <div className="login-option">
              <form action="/create" method="get" className="option1">
                <Link to="/create">
                  <Button type="submit" className="option1-inner">
                    회원가입
                  </Button>
                </Link>
              </form>
              <form action="/id/search" method="get" className="option2">
                <Link to="/find_id">
                  <Button type="submit" className="option2-inner">
                    아이디 찾기
                  </Button>
                </Link>
              </form>
              <form action="/pw/search" method="get" className="option3">
                <Link to="/find_pass">
                  <Button type="submit" className="option3-inner">
                    비밀번호 찾기
                  </Button>
                </Link>
              </form>
            </div>
          </div>
        </div>
      </body>
    );
  }
}

export default Login_form;
