//import { Link, Outlet } from "react-router-dom"; // 스타일을 주기위한 것(사용 안 함)
import { SideMenu, TopMenu } from "./Menus";
import "./style.css";
import Daum from "image/daum.jpg";
import Google from "image/google.png";
import Kakao from "image/kakao.png";
import Naver from "image/naver.png";

// sidemenu가 페이지마다 나오게하기 위해 미리 작업
const PageTemplate = ({ children }) => (
  <div className="page">
    <SideMenu />
    {children}
  </div>
);

// 첫 화면(메인 화면) 섹션 편집
export const Main = () => (
  <PageTemplate>
    <section className="main">
      <TopMenu />
      <div>어서오세요</div>
    </section>
  </PageTemplate>
);

// 정보수정부분 섹션 편집
export const Info_modify = () => (
  <PageTemplate>
    <section className="info_modify">
      <TopMenu />
      <div>정보 수정하는 부분</div>
    </section>
  </PageTemplate>
);

// 일정부분 섹션 편집
export const Schedule = () => (
  <PageTemplate>
    <section className="schedule">
      <TopMenu />
      <h1>[Schedule]</h1>
      <p>This area is for schedule</p>
    </section>
  </PageTemplate>
);

// 상품부분 섹션 편집
export const Products = () => (
  <PageTemplate>
    <section className="products">
      <TopMenu />
      <tbody>
        <table>
          <td>
            <img src="/image/daum.jpg" alt="daum.jpg"></img>
          </td>

          <td>
            <img src={Google} alt="google.png"></img>
          </td>
          <td>
            <img src={Kakao} alt="kakao.png"></img>
          </td>
          <td>
            <img src={Naver} alt="naver.png"></img>
          </td>
        </table>
      </tbody>
    </section>
  </PageTemplate>
);

// 화면 전환 시 오류나면 출력
export const Whoops404 = ({ location }) => (
  <div className="whoops-404">
    <h1>Resource not found at '{location.pathname}'</h1>
  </div>
);
