import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link, Route, Routes, Navigate } from "react-router-dom";
import "./create_address.css";

interface IProps {}

class Find_id extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      phone: "",
      member_id_list: [],
    };
  }

  state = {
    phone: "",
    member_id_list: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  check = () => {
    Axios.post("http://localhost:8000/find_id", {
      phone: this.state.phone,
    })
      .then((res) => {
        this.setState({
          phone: "",
        });
      })
      .catch((e) => {
        console.error(e);
        alert("fail");
      });

    Axios.get("http://localhost:8000/find_id", {})
      .then((res) => {
        const { data } = res;
        this.setState({
          member_id_list: data,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    return (
      <body>
        <div className="box">
          <form method="post" className="create-address-form">
            <fieldset className="inner-form">
              <legend className="create_address-member">아이디 찾기</legend>

              <input
                type="text"
                name="phone"
                value={this.state.phone}
                onChange={this.handleChange}
                placeholder="전화번호를 입력해주세요"
              ></input>
              <Link to="/">
                <Button variant="info" onClick={this.check}>
                  확인
                </Button>
              </Link>
            </fieldset>
          </form>
        </div>
      </body>
    );
  }
}

export default Find_id;
