import "./M_P.css";
import "./info_modify.css";

import { useLocation, Link } from "react-router-dom";
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import Button from "react-bootstrap/button";
import { Component, useState } from "react";
import Axios from "axios";

function Schedule() {
  const location = useLocation();
  const [curpass, set_curpass] = useState(location.state.member_pass);
  const [curnickname, set_curnickname] = useState(location.state.nickname);
  const [nickname, set_nickname] = useState("");

  const [fcheck_pass, set_fcheck_pass] = useState("");
  const [scheck_pass, set_scheck_pass] = useState("");
  const [change_pass, set_change_pass] = useState("");
  const first_handleChange = (e: any) => {
    set_fcheck_pass(e.target.value);
  };

  const second_handleChange = (e: any) => {
    set_scheck_pass(e.target.value);
  };

  const change_handleChange = (e: any) => {
    set_change_pass(e.target.value);
  };
  const nickname_handleChange = (e: any) => {
    set_nickname(e.target.value);
  };
  const change_password = () => {
    if (curpass === fcheck_pass) {
      if (scheck_pass === change_pass) {
        Axios.post("http://localhost:8000/info_modify/change_pass", {
          id: location.state.member_id,
          change_pass: change_pass,
        })
          .then((res) => {
            alert("비밀번호를 변경했습니다.");
            set_curpass(change_pass);
            set_fcheck_pass("");
            set_scheck_pass("");
            set_change_pass("");
          })
          .catch((e) => {
            console.error(e);
          });
      } else {
        alert("변경할 비밀번호가 다릅니다.");
      }
    } else {
      alert("현재 비밀번호가 다릅니다.");
    }
  };
  const logout = () => {
    document.location.href = "/";
  };
  return (
    <section className="schedule">
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">{location.state.nickname}</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                className="introduction-text"
                value={location.state.intro}
                placeholder="자기소개를 작성하세요"
              ></textarea>
              <br></br>
              <input type="submit" value="수정"></input>
              <input type="submit" value="저장"></input>
            </div>
            <div>
              <input type="submit" value="logout" onClick={logout}></input>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link
          to={"/main/Info_modify"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          정보 수정
        </Link>
        <Link
          to={"/main/Schedule"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          일정
        </Link>
        <Link
          to={"/main/Products"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          상품
        </Link>
      </nav>
      <div className="box">
        <form>
          <fieldset className="inner-form">
            <legend className="title">비밀번호 변경</legend>
            <div>
              <span className="subtitle3">이전 비밀번호 : </span>
              <input
                type="password"
                name="fcheck_pass"
                className="member_pass"
                placeholder="기존 비밀번호를 입력해주세요"
                value={fcheck_pass}
                onChange={first_handleChange}
              ></input>
            </div>
            <div>
              <span className="subtitle2">변경할 비밀번호 : </span>
              <input
                type="password"
                name="scheck_pass"
                className="member_pass"
                placeholder="변경할 비밀번호를 입력해주세요"
                value={scheck_pass}
                onChange={second_handleChange}
              ></input>
            </div>
            <div>
              <span className="subtitle">변경할 비밀번호(확인) : </span>
              <input
                type="password"
                name="change_pass"
                className="member_pass"
                placeholder="변경할 비밀번호를 입력해주세요"
                value={change_pass}
                onChange={change_handleChange}
              ></input>
            </div>
            <Button variant="info" onClick={change_password} className="btn">
              확인
            </Button>
          </fieldset>
        </form>
      </div>

      <div className="box">
        <form>
          <fieldset className="inner-form">
            <legend className="title">개인정보 변경</legend>
            <div>
              <span className="subtitle1">닉네임 : </span>
              <input
                type="text"
                name="nickname"
                className="create_nickname"
                placeholder="변경할 닉네임을 입력해주세요"
                value={nickname}
                onChange={nickname_handleChange}
              ></input>
            </div>
          </fieldset>
        </form>
      </div>
    </section>
  );
}

export default Schedule;
