//import { Link, Outlet } from "react-router-dom"; // 스타일을 주기위한 것(사용 안 함)
import { SideMenu, TopMenu } from "./Menus";
import "./style.css";

// sidemenu가 페이지마다 나오게하기 위해 미리 작업
const PageTemplate = ({ children }) => (
  <div className="page">
    <SideMenu />
    {children}
  </div>
);

const Log = () => (
  <div className="login">
    <div className="login-form">
      <div className="login-top">없어</div>
    </div>
  </div>
);

// 첫 화면(메인 화면) 섹션 편집
export const Main = () => (
  <section className="main">
    <TopMenu />
    <div>일단 첫 화면</div>
    <div className="login">
      <h3>login</h3>
      <input type="submit" id="btn123" value="버튼" onClick={onclick}></input>
      <div className="login-form">
        <div className="login-top">
          <input type="text" placeholder="ID"></input> <br></br>
          <input type="password" placeholder="PassWord"></input>
        </div>
        <br></br>
        <input type="submit" value="login" className="login-btn"></input>
        <br></br>
        <input type="submit" value="join" className="join-btn"></input>
      </div>
    </div>
  </section>
);

// 정보수정부분 섹션 편집
export const Info_modify = () => (
  <PageTemplate>
    <section className="info_modify">
      <TopMenu />
      <div>정보 수정하는 부분</div>
    </section>
  </PageTemplate>
);

// 일정부분 섹션 편집
export const Schedule = () => (
  <PageTemplate>
    <section className="schedule">
      <TopMenu />
      <h1>[Schedule]</h1>
      <p>This area is for schedule</p>
    </section>
  </PageTemplate>
);

// 상품부분 섹션 편집
export const Products = () => (
  <PageTemplate>
    <section className="products">
      <TopMenu />
      <h1>[Products]</h1>
      <p>This area is for products</p>
    </section>
  </PageTemplate>
);

// 화면 전환 시 오류나면 출력
export const Whoops404 = ({ location }) => (
  <div className="whoops-404">
    <h1>Resource not found at '{location.pathname}'</h1>
  </div>
);
