import { Component } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link, Route, Routes, Navigate } from "react-router-dom";
import "./create_address.css";

interface IProps {}

const Uselist = ({ id, phone }: { id: string; phone: string }) => {
  if (phone == check) {
    return (
      <tr>
        <td>{id}</td>
      </tr>
    );
  }
};

class Find_id extends Component<IProps> {
  constructor(props: any) {
    super(props);
    this.state = {
      phone: "",
      memberIdList: [],
    };
  }

  state = {
    phone: "",
    memberIdList: [],
  };

  handleChange = (e: any) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  check = () => {
    Axios.get("http://localhost:8000/find_id1", {})
      .then((res) => {
        const { data } = res;
        this.setState({
          memberIdList: data,
        });
        alert("succss");
      })
      .catch((e) => {
        console.error(e);
      });
  };

  render() {
    const { memberIdList }: { memberIdList: any } = this.state;

    return (
      <body>
        <div className="box">
          <form method="post" className="create-address-form">
            <fieldset className="inner-form">
              <legend className="create_address-member">아이디 찾기</legend>
              <div>
                <input
                  type="text"
                  name="phone"
                  value={this.state.phone}
                  onChange={this.handleChange}
                  placeholder="전화번호를 입력해주세요"
                ></input>
                <Button variant="info" onClick={this.check}>
                  확인
                </Button>
              </div>
              <tbody>
                {memberIdList.map((v: any) => {
                  return <Uselist id={v.MEMBER_ID} phone={v.PHONE} />;
                })}
              </tbody>
              <Link to="/">
                <Button variant="info">확인</Button>
              </Link>
            </fieldset>
          </form>
        </div>
      </body>
    );
  }
}

export default Find_id;
