import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../Style/PostBoard.css";
/* import TopMenu from './TopMenu'; */
import Login from "../Login";
import PostFunc from "../PostFunc";
import PostTr from "./Test_PostTr";
import PostModal from "./Test_PostModal";
import io from "socket.io-client";

const PostBoard_Data_Save = require("../Function/PostBoard_Data_Save.js");

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

const PostBoard: React.FC = () => {
  const [notice, setNotice] = useState<PostItem[]>([]);
  const [complaint, setComplaint] = useState<PostItem[]>([]);
  const [selected, setSelected] = useState<PostItem | null>(null);
  const [modalOn, setModalOn] = useState<boolean>(false);
  const [cur_num, setCurNum] = useState<number>(1);
  const [start, setStart] = useState<number>(0);

  const handleCancel = () => {
    setModalOn(false);
  };

  const handleEditSumit = (item: PostItem) => {
    setModalOn(false);
  };

  const location = useLocation();
  const userInfo = { ...location.state };

  const navigate = useNavigate();
  function Go_Back() {
    navigate("/test_page", {
      state: { id: userInfo.id },
    });
  }

  function use_notice_save_data(data: any) {
    return new Promise((resolve, reject) => {
      var data_num = 0;
      while (true) {
        if (data.user_data[data_num] === undefined) {
          break;
        } else {
          const post: PostItem = {
            order: data.user_data[data_num].note_order,
            id: data.user_data[data_num].note_id,
            name: data.user_data[data_num].note_name,
            date: data.user_data[data_num].note_date,
            title: data.user_data[data_num].note_title,
            content: data.user_data[data_num].note_content,
          };
          notice[data_num] = post;
          data_num++;
        }
      }
      resolve(0);
    });
  }

  function use_complaint_save_data(data: any) {
    return new Promise((resolve, reject) => {
      var data_num = 0;
      while (true) {
        if (data.user_data[data_num] === undefined) {
          break;
        } else {
          const post: PostItem = {
            order: data.user_data[data_num].complaint_order,
            id: data.user_data[data_num].complaint_id,
            name: data.user_data[data_num].complaint_name,
            date: data.user_data[data_num].complaint_date,
            title: data.user_data[data_num].complaint_title,
            content: data.user_data[data_num].complaint_content,
          };
          complaint[data_num] = post;
          data_num++;
        }
      }
      resolve(0);
    });
  }

  async function save_notice_data() {
    const socket = io("http://localhost:3001", { transports: ["websocket"] });
    await PostBoard_Data_Save.Send_Notice_Five_Data(socket, cur_num);
    const data = await PostBoard_Data_Save.Rec_Notice_Five_Data(socket);
    await use_notice_save_data(data);
    // setStart(1); // 원인은 모르겠지만 해당 데이터가 변경되어야 표시가 됨
    await PostBoard_Data_Save.Send_Complaint_Five_Data(socket, cur_num);
    const use = await PostBoard_Data_Save.Rec_Complaint_Five_Data(socket);
    await use_complaint_save_data(use);
    setStart(1); // 원인은 모르겠지만 해당 데이터가 변경되어야 표시가 됨
    socket.disconnect();
  }

  useEffect(() => {
    const load = async () => {
      await save_notice_data();
    };
    load();
  }, []);

  return (
    <div className="body_box">
      <div className="bottom_box">
        <div className="left_box">
          <div className="main_area">
            <div className="post_area">
              <div className="post_table">
                <h1 className="h1">공지사항 게시글 리스트</h1>
                <table className="table">
                  <thead className="thead">
                    <tr className="tr1">
                      <th className="contents1">순번</th>
                      <th className="contents1">작성자</th>
                      <th className="contents1">작성날짜</th>
                      <th className="contents1">제목</th>
                      <th className="contents1">작성내용</th>
                      <th className="contents1">편집</th>
                      <th className="contents1">삭제</th>
                    </tr>
                  </thead>
                  <PostTr info={notice} user_id={userInfo.id} />
                </table>
              </div>
            </div>
            <div className="complain_area">
              <div className="complain_table">
                <h1 className="h1">불만사항 게시글 리스트</h1>
                <table className="table">
                  <thead className="thead">
                    <tr className="tr1">
                      <th className="contents1">순번</th>
                      <th className="contents1">작성자</th>
                      <th className="contents1">작성날짜</th>
                      <th className="contents1">제목</th>
                      <th className="contents1">작성내용</th>
                      <th className="contents1">편집</th>
                      <th className="contents1">삭제</th>
                    </tr>
                  </thead>
                  <PostTr info={complaint} user_id={userInfo.id} />
                </table>
              </div>
              <button type="button" onClick={Go_Back}>
                이전으로
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostBoard;
