import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../../Style/test_TabMenu.css";

const TabMenu = () => {
  const [isHovering, setIsHovering] = useState(false);

  const handleMouseOver = () => {
    setIsHovering(true);
  };

  const handleMouseOut = () => {
    setIsHovering(false);
  };

  const location = useLocation();
  const userInfo = { ...location.state };

  const navigate = useNavigate();

  function Go_Notice() {
    navigate("/test_add", {
      state: { id: userInfo.id, title: "공지사항" },
    });
  }

  function Go_Complaint() {
    navigate("/test_add", {
      state: { id: userInfo.id, title: "불만사항" },
    });
  }

  function Go_note_comp() {
    navigate("/test", {
      state: { id: userInfo.id },
    });
  }

  return (
    <div className="topmenubar">
      <nav className="navi">
        <ul>
          <li
            className={isHovering ? "topmenu_hover" : "topmenu"}
            onMouseOver={handleMouseOver}
          >
            공지사항 및 불만사항
            <ul
              className={isHovering ? "topmenu_hover_ul" : "topmenu_ul"}
              onMouseOut={handleMouseOut}
            >
              <li type="button" onClick={Go_Notice}>
                공지사항 작성
              </li>
              <li type="button" onClick={Go_Complaint}>
                불만사항 작성
              </li>
              <li type="button" onClick={Go_note_comp}>
                공지사항, 불만사항
              </li>
            </ul>
          </li>
          <li
            className={isHovering ? "topmenu_hover" : "topmenu"}
            onMouseOver={handleMouseOver}
          >
            메뉴2
            <ul
              className={isHovering ? "topmenu_hover_ul" : "topmenu_ul"}
              onMouseOut={handleMouseOut}
            >
              <li>메뉴2-1</li>
              <li>메뉴2-2</li>
              <li>메뉴2-3</li>
            </ul>
          </li>
          <li
            className={isHovering ? "topmenu_hover" : "topmenu"}
            onMouseOver={handleMouseOver}
          >
            메뉴3
            <ul
              className={isHovering ? "topmenu_hover_ul" : "topmenu_ul"}
              onMouseOut={handleMouseOut}
            >
              <li>메뉴3-1</li>
              <li>메뉴3-2</li>
              <li>메뉴3-3</li>
            </ul>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default TabMenu;
