import "./M_P.css";
import "./info_modify.css";

import { useLocation, Link } from "react-router-dom";
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import { useState } from "react";
import Axios from "axios";

function Schedule() {
  const location = useLocation();
  const [intro, set_intro] = useState(location.state.intro);
  const [curintro, set_curintro] = useState(location.state.intro);
  const [cur_date, set_cur_date] = useState("");
  const [date_schedule, set_date_schedule] = useState("");

  const save_intro = () => {
    Axios.post("http://localhost:8000/save_intro", {
      id: location.state.member_id,
      intro: intro,
    })
      .then((res) => {
        alert("저장했습니다.");
        set_curintro(intro);
        set_intro(intro);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const search_schedule = () => {
    Axios.post("http://localhost:8000/search_schedule", {
      id: location.state.member_id,
      date: cur_date,
    })
      .then((res) => {
        alert(res.data[0]);
        if (res.data[0] === undefined) {
          set_date_schedule("");
        } else {
          set_date_schedule(res.data[0].plan_content);
        }
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const change_schedule = () => {
    Axios.post("http://localhost:8000/search_schedule", {
      id: location.state.member_id,
      date: cur_date,
    })
      .then((res) => {
        alert(location.state.member_id);
        alert(cur_date);
        alert(date_schedule);
        if (res.data[0] !== undefined) {
          alert("update");
          Axios.post("http://localhost:8000/change_schedule", {
            id: location.state.member_id,
            date: cur_date,
            date_schedule: date_schedule,
          })
            .then((res) => {
              alert("저장했습니다.");
            })
            .catch((e) => {
              console.error(e);
            });
        } else {
          alert("insert");
          Axios.post("http://localhost:8000/insert_schedule", {
            id: location.state.member_id,
            date: cur_date,
            schedule: date_schedule,
          })
            .then((res) => {
              alert("저장했습니다.");
            })
            .catch((e) => {
              console.error(e);
            });
        }
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const date_schedule_handleChange = (e: any) => {
    set_date_schedule(e.target.value);
  };

  const cur_date_handleChange = (e: any) => {
    set_cur_date(e.target.value);
  };

  const intro_handleChange = (e: any) => {
    set_intro(e.target.value);
  };

  const logout = () => {
    document.location.href = "/";
  };
  // 여기부터 변경

  return (
    <section className="schedule">
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">{location.state.nickname}</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                className="introduction-text"
                name="intro"
                value={intro}
                onChange={intro_handleChange}
                placeholder="자기소개를 작성하세요"
              ></textarea>
              <br></br>
              <input type="submit" onClick={save_intro} value="수정"></input>
            </div>
            <div>
              <input type="submit" value="logout" onClick={logout}></input>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link
          to={"/main/Info_modify"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: curintro,
          }}
        >
          정보 수정
        </Link>
        <Link
          to={"/main/Schedule"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: curintro,
          }}
        >
          일정
        </Link>
        <Link
          to={"/main/Products"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: curintro,
          }}
        >
          상품
        </Link>
      </nav>
      <div>
        <input
          type="date"
          value={cur_date}
          onChange={cur_date_handleChange}
        ></input>
        <button type="submit" onClick={search_schedule}>
          검색
        </button>
      </div>
      <div></div>
      <div>
        <textarea
          name="date_schedule"
          placeholder="일정을 입력하세요"
          value={date_schedule}
          onChange={date_schedule_handleChange}
        ></textarea>
      </div>
      <button type="submit" onClick={change_schedule}>
        수정
      </button>
      <div></div>
    </section>
  );
}

export default Schedule;
