import { Component, useState } from "react";
import Button from "react-bootstrap/Button";
import Axios from "axios";
import { Link } from "react-router-dom";
import "./M_P.css";
import { useLocation } from "react-router-dom";
import character_img from "./character_img.jpg"; // 이미지 불러온 것

function Main() {
  const location = useLocation();
  const [member_id, set_member_id] = useState("");
  const [member_pass, set_member_pass] = useState("");
  const [phone, set_phone] = useState("");
  const [gender, set_gender] = useState(0);
  const [nickname, set_nickname] = useState("");
  const [SSN, set_SSN] = useState("");
  const [intro, set_intro] = useState("");
  const check = () => {
    Axios.post("http://localhost:8000/current_id", {
      id: location.state.using_id,
    })
      .then((res) => {
        set_member_id(res.data[0].member_id);
        set_member_pass(res.data[0].member_pass);
        set_phone(res.data[0].phone);
        set_gender(res.data[0].gender);
        set_nickname(res.data[0].nickname);
        set_SSN(res.data[0].SSN);
      })
      .catch((e) => {
        console.error(e);
      });
    Axios.post("http://localhost:8000/current_intro", {
      id: location.state.using_id,
    })
      .then((res) => {
        set_intro(res.data[0].intro);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  check();

  const save = () => {
    Axios.post("http://localhost:8000/save_intro", {
      id: location.state.using_id,
      intro: intro,
    })
      .then((res) => {
        set_intro(res.data[0].intro);
      })
      .catch((e) => {
        console.error(e);
      });
  };

  const change = () => {
    alert("change");
  };

  const intro_handleChange = (e: any) => {
    set_intro(e.target.value);
  };

  const logout = () => {
    document.location.href = "/";
  };
  return (
    <section className="main">
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">{nickname}</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                typeof="text"
                className="introduction-text"
                name="intro"
                value={intro}
                onChange={intro_handleChange}
                placeholder="자기소개를 작성하세요"
              ></textarea>
              <br></br>
              <input type="submit" value="저장" onClick={save}></input>
            </div>
            <div>
              <input type="submit" value="logout" onClick={logout}></input>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link
          to={"/main/Info_modify"}
          state={{
            member_id: member_id,
            member_pass: member_pass,
            phone: phone,
            gender: gender,
            nickname: nickname,
            SSN: SSN,
            intro: intro,
          }}
        >
          정보 수정
        </Link>
        <Link
          to={"/main/Schedule"}
          state={{
            member_id: member_id,
            member_pass: member_pass,
            phone: phone,
            gender: gender,
            nickname: nickname,
            SSN: SSN,
            intro: intro,
          }}
        >
          일정
        </Link>
        <Link
          to={"/main/Products"}
          state={{
            member_id: member_id,
            member_pass: member_pass,
            phone: phone,
            gender: gender,
            nickname: nickname,
            SSN: SSN,
            intro: intro,
          }}
        >
          상품
        </Link>
      </nav>
      <div>어서오세요</div>
    </section>
  );
}

export default Main;
