import "./M_P.css";
import { Link, useLocation } from "react-router-dom";
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import Axios from "axios";
import { Component, useState } from "react";

function Info_modify() {
  const location = useLocation();
  const [fcheck_pass, set_fcheck_pass] = useState("");
  const [scheck_pass, set_scheck_pass] = useState("");
  const [change_pass, set_change_pass] = useState("");
  const [phone, set_phone] = useState("");
  const [gender, set_gender] = useState(0);
  const [nickname, set_nickname] = useState("");
  const [SSN, set_SSN] = useState("");

  const logout = () => {
    document.location.href = "/";
  };

  const f_handleChange = (v: any) => {
    set_fcheck_pass(v);
  };

  return (
    <section className="info_modify">
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">{location.state.nickname}</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                className="introduction-text"
                value={location.state.intro}
                placeholder="자기소개를 작성하세요"
              ></textarea>
              <br></br>
              <input type="submit" value="수정"></input>
              <input type="submit" value="저장"></input>
            </div>
            <div>
              <input type="submit" value="logout" onClick={logout}></input>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link
          to={"/main/Info_modify"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          정보 수정
        </Link>
        <Link
          to={"/main/Schedule"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          일정
        </Link>
        <Link
          to={"/main/Products"}
          state={{
            member_id: location.state.member_id,
            member_pass: location.state.member_pass,
            phone: location.state.phone,
            gender: location.state.gender,
            nickname: location.state.nickname,
            SSN: location.state.SSN,
            intro: location.state.intro,
          }}
        >
          상품
        </Link>
      </nav>
      <div>
        <div>비밀번호 변경</div>
        <div>
          <span className="subtitle">이전 비밀번호 : </span>
          <input
            type="password"
            name="fcheck_pass"
            className="member_pass"
            placeholder="비밀번호를 입력해주세요"
            value={fcheck_pass}
            onChange={f_handleChange}
          ></input>
        </div>

        <div>이외 정보 변경</div>
      </div>
    </section>
  );
}

export default Info_modify;
