import Button from "react-bootstrap/Button";
import Axios from "axios";
import Login_form from "./login_form";
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import { Link, NavLink } from "react-router-dom"; // 스타일을 주기위한 것

// sidemenu가 페이지마다 나오게하기 위해 미리 작업
export const PageTemplate = ({ children }) => (
  <div className="page">
    <SideMenu />
    {children}
  </div>
);

export const TopMenu = () => (
  <nav className="top-menu">
    <Link to="/main/Info_modify">정보 수정</Link>
    <Link to="/main/Schedule">일정</Link>
    <Link to="/main/Products">상품</Link>
  </nav>
);

const check = () => {
  document.location.href = "/";
};

export const SideMenu = () => (
  <div className="side-menu">
    <div className="info">
      <div className="character">
        <img src={character_img} className="img1" alt="character_img.jpg" />
      </div>
      <div className="nickname">닉네임</div>
      <div className="info-script">
        <div className="introduction">자기소개</div>
        <textarea
          className="introduction-text"
          placeholder="자기소개를 작성하세요"
        ></textarea>
        <br></br>
        <input type="submit" value="수정"></input>
        <input type="submit" value="저장"></input>
      </div>
      <div>
        <input type="submit" value="logout" onClick={check}></input>
      </div>
    </div>
  </div>
  //https://velog.io/@willshowitself/React-%ED%8E%98%EC%9D%B4%EC%A7%80%EB%A5%BC-%EC%9D%B4%EB%8F%99%ED%95%A0-%EB%95%8C-%EB%8D%B0%EC%9D%B4%ED%84%B0%EB%8F%84-%ED%95%A8%EA%BB%98-%EB%84%98%EA%B8%B0%EB%8A%94-%EB%B0%A9%EB%B2%95
);
