import "./M_P.css";
import { Link } from "react-router-dom";
import Daum from "./image/daum.jpg";
import Google from "./image/google.png";
import Kakao from "./image/kakao.png";
import Naver from "./image/naver.png";
import character_img from "./character_img.jpg"; // 이미지 불러온 것
import Axios from "axios";

function Products() {
  const location = useLocation();
  const check = () => {
    Axios.post("http://localhost:8000/current_id", {
      id: location.state.using_id,
    })
      .then((res) => {
        console.log(res.data[0].member_id);
        console.log(res.data[0].member_pass);
        console.log(res.data[0].phone);
        console.log(res.data[0].gender);
        console.log(res.data[0].nickname);
        console.log(res.data[0].SSN);
      })
      .catch((e) => {
        console.error(e);
      });
  };
  check();
  const logout = () => {
    document.location.href = "/";
  };
  return (
    <section className="products">
      <div className="page">
        <div className="side-menu">
          <div className="info">
            <div className="character">
              <img
                src={character_img}
                className="img1"
                alt="character_img.jpg"
              />
            </div>
            <div className="nickname">닉네임</div>
            <div className="info-script">
              <div className="introduction">자기소개</div>
              <textarea
                className="introduction-text"
                placeholder="자기소개를 작성하세요"
              ></textarea>
              <br></br>
              <input type="submit" value="수정"></input>
              <input type="submit" value="저장"></input>
            </div>
            <div>
              <input type="submit" value="logout" onClick={logout}></input>
            </div>
          </div>
        </div>
      </div>
      <nav className="top-menu">
        <Link to="/main/Info_modify">정보 수정</Link>
        <Link to="/main/Schedule">일정</Link>
        <Link to="/main/Products">상품</Link>
      </nav>
      <tbody>
        <table>
          <td>
            <a href="https://www.daum.net">
              <img src={Daum} className="product_img" alt="daum.jpg"></img>
              <br></br>
              다음
            </a>
          </td>
          <td>
            <a href="https://www.google.com">
              <img src={Google} className="product_img" alt="google.png"></img>
              <br></br>
              구글
            </a>
          </td>
          <td>
            <a href="https://www.kakaocorp.com/page/">
              <img src={Kakao} className="product_img" alt="kakao.png"></img>
              <br></br>
              카카오
            </a>
          </td>
          <td>
            <a href="https://www.naver.com">
              <img src={Naver} className="product_img" alt="naver.png"></img>
              <br></br>
              네이버
            </a>
          </td>
        </table>
      </tbody>
    </section>
  );
}

export default Products;
